import { useState } from "react";

export default (defaultValue) => {
  const [value, setValue] = useState(defaultValue);

  const onChange = (event) => {
    const {
      target: { id }
    } = event;
    console.log(id);
    setValue(id);
  };

  return { value, onChange, setValue };
};
