import React, { useState } from "react";
import AuthPresenter from "./AutherPresenter";
import useInput from "../../Hooks/useInput";
import { useMutation } from "react-apollo-hooks";
import { LOG_IN, CREATE_ACCOUNT } from "./AuthQueries";
import { toast } from "react-toastify";

export default () => {
  const [action, setAction] = useState("logIn"); //Hooks
  const username = useInput("");
  const firstName = useInput("");
  const lastName = useInput("");
  const email = useInput("");
  const [requestSecretMutation] = useMutation(LOG_IN, {
    variables: { email: email.value }
  });

  const [createAccountMutaion] = useMutation(CREATE_ACCOUNT, {
    variables: {
      email: email.value,
      username: username.value,
      firstName: firstName.value,
      lastName: lastName.value
    }
  });

  const onSubmit = async (event) => {
    event.preventDefault();
    if (action === "logIn") {
      if (email.value !== "") {
        try {
          const { requestSecret } = await requestSecretMutation();
          if (!requestSecret) {
            toast.error("해당 하는 계정이 없습니다 계정을 등록하세요!");
            setTimeout(() => setAction("signUp"), 3000);
          }
        } catch {
          toast.error("요청을 보낼수 없습니다. 다시 시도해주세요.");
        }
      } else {
        toast.error("해당 메일은 존재하지 않는 계정입니다.");
      }
    } else if (action === "signUp") {
      if (
        email.value !== "" &&
        username.value !== "" &&
        firstName.value !== "" &&
        lastName.value !== ""
      ) {
        try {
          const { createAccount } = await createAccountMutaion();
        } catch {
          toast.error("계정을 생성할 수 없습니다. 다시 시도해주세요.");
        }
      }
    } else {
      toast.error("모든 항목의 기입이 필요합니다.");
    }
  };

  return (
    <AuthPresenter
      setAction={setAction}
      action={action}
      username={username}
      firstName={firstName}
      lastName={lastName}
      email={email}
      onSubmit={onSubmit}
    />
  );
};
