import React, { useState } from "react";
export default ({
  id,
  avatar,
  username,
  location,
  isLiked,
  files,
  likeCount
}) => {
  const [isLikedS, setIsLiked] = useState(isLiked);
  const [likeCountS, setLikeCountS] = useState(likeCountS);

  const toggleLike = () => {
    toggleLikeMutation();
    if (isLikedS === true) {
      setIsLiked(false);
      setLikeCountS(likeCountS - 1);
    } else {
      setIsLiked(true);
      setLikeCountS(likeCountS + 1);
    }
  };

  return null;
};
