import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import FatText from "../../Components/FatText";
import TextareaAutosize from "react-autosize-textarea/lib";
import Avatar from "../../Components/Avatar";
import { useMutation } from "react-apollo-hooks";
import { TOGGLE_LIKE } from "../../Components/Post/PostQuerise";
import {
  HeartFull,
  HeartEmpty,
  Comment as CommentIcon
} from "../../Components/Icons";

const Post = styled.div`
  ${(props) => props.theme.whiteBox};
  width: 100%;
  max-width: 600px;
  user-select: none;
  margin-bottom: 25px;
  a {
    color: inherit;
  }
`;

const Header = styled.header`
  padding: 10px;
  display: flex;
  align-items: center;
`;

const UserColumn = styled.div`
  margin-left: 10px;
`;

const Location = styled.span`
  display: block;
  margin-top: 5px;
  font-size: 12px;
`;

const Files = styled.div`
  position: relative;
  padding-top: 100%;
`;

const File = styled.img`
  max-width: 100%;
  width: 100%;
  height: 600px;
  position: absolute;
  top: 0;
  background-image: url(${(props) => props.src});
  background-size: cover;
  background-position: center;
  opacity: ${(props) => (props.showing ? 1 : 0)};
  transition: opacity 0.5s linear;
`;

const Button = styled.span`
  cursor: pointer;
`;

const Meta = styled.div`
  padding: 15px;
`;

const Buttons = styled.div`
  ${Button} {
    &:first-child {
      margin-right: 10px;
    }
  }
  margin-bottom: 10px;
`;

const TimeStamp = styled.span`
  font-weight: 400;
  text-transform: uppercase;
  opacity: 0.5;
  display: block;
  font-size: 12px;
  margin: 10px 0px;
  padding-bottom: 10px;
  border-bottom: ${(props) => props.theme.lightGreyColor} 1px solid;
`;

const Textarea = styled(TextareaAutosize)`
  border: none;
  width: 100%;
  resize: none;
  font-size: 14px;
  &:focus {
    outline: none;
  }
`;

const Comments = styled.ul`
  margin-top: 10px;
`;

const Comment = styled.li`
  margin-bottom: 7px;
  span {
    margin-right: 5px;
  }
`;

const DeleteButtonTag = styled.span`
  margin-bottom: 7px;
  span {
    margin-right: 5px;
  }
`;

export default ({
  id,
  avatar,
  username,
  location,
  isLiked,
  files,
  likeCount,
  comments
}) => {
  const [isLikedS, setIsLiked] = useState(isLiked);
  const [likeCountS, setLikeCountS] = useState(likeCountS);

  const [toggleLikeMutation] = useMutation(TOGGLE_LIKE, {
    variables: { postId: id }
  });

  const toggleLike = () => {
    toggleLikeMutation();
    if (isLikedS === true) {
      setIsLiked(false);
      setLikeCountS(likeCountS - 1);
    } else {
      setIsLiked(true);
      setLikeCountS(likeCountS + 1);
    }
  };

  const CommentDeadProcessing = async (data) => {
    const commentBox = comments.map((comment) => comment.id);
    const dataValue = commentBox.indexOf(data);

    try {
      if (dataValue !== null) {
        await deleteCommentMutation({
          variables: { commentId: data }
        });
        comments.splice(dataValue, 1);
        setDeleteCommentS(dataValue);
      } else {
        console.log("<svg>의 id를 가져올 수 없었습니다");
      }
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    console.log("useEffect용 console");
  }, [deleteCommentS]);

  return (
    <Post>
      <Header>
        <Avatar size="sm" url={avatar} />
        <UserColumn>
          <Link to={`/${username}`}>
            <FatText text={username} />
          </Link>
          <Location>{location}</Location>
        </UserColumn>
      </Header>
      <Files>
        {files &&
          files.map((file, index) => (
            <File id={file.id} key={file.id} src={file.url} />
          ))}
      </Files>
      <Meta>
        <Buttons>
          <Button onClick={toggleLike}>
            {isLiked ? <HeartFull /> : <HeartEmpty />}
          </Button>
          <Button>
            <CommentIcon />
          </Button>
        </Buttons>
        <FatText text={likeCount === 1 ? "1 like" : `${likeCount} likes`} />
        {comments && (
          <Comments>
            {comments.map((comment) => (
              <Comment key={comment.id}>
                <FatText text={comment.user.username} />
                {comment.text}
                <DeleteButtonTag
                  onClick={async (data) => {
                    const originalComment = await data.target.id;
                    const changeComment = await originalComment.substring(
                      7,
                      32
                    );

                    return CommentDeadProcessing(changeComment);
                  }}
                >
                  <DeleteButton id={comment.id} />
                </DeleteButtonTag>
              </Comment>
            ))}
            {selfComments.map((comment) => (
              <Comment key={comment.id}>
                <FatText text={comment.user.username} />
                {comment.text}
              </Comment>
            ))}
          </Comments>
        )}
        <TimeStamp>{createdAt}</TimeStamp>
        <form>
          <Textarea
            onKeyPress={onkeyPress}
            placeholder={"댓글을 입력하세요..."}
            value={newComment.value}
            onChange={newComment.onChange}
          />
        </form>
      </Meta>
    </Post>
  );
};
