import { gql } from "apollo-boost";

export const SEARCH_POSTS = gql`
  query search(term: String!) {
    searchPost(term: $term) {
      files {
        url
      }
      likeCount
    }
    searchUSer(term:$term){
        avatar
        username
        isFollowing

    }
  }
`;
